import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAddressCard } from '@fortawesome/free-solid-svg-icons'

class Address extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             address : this.props.address,
             editMode : 'N'
        }
    }
    

    render() {
        const { address } = this.state;
        const { editMode } = this.state;
        return (
                <div>
                    {editMode == 'N' ?
                        <div>
                            {address.id ? 
                                <div>
                                    <i><FontAwesomeIcon icon={faAddressCard} /></i> 
                                    <input type="button" value="EDIT" onClick={() => this.setState({editMode : 'Y'})}/>
                                    {address.line1 ? 
                                        <div><label>Line 1 : </label>{ address.line1 }</div> 
                                    : ''}
                                    {address.line2 ? 
                                        <div><label>Line 2 : </label>{ address.line2 }</div> 
                                    : ''}
                                    <div>
                                    {address.city ? 
                                        <span><label>City : </label>{ address.city }</span> 
                                    : ''}
                                    {address.taluka ? 
                                        <span style={{padding: 1 + 'em'}}><label>Taluka : </label>{ address.taluka }</span> 
                                    : ''}
                                    </div>
                                    {address.district ? 
                                        <span><label>District : </label>{ address.district }</span> 
                                    : ''}
                                    {address.state ? 
                                        <span style={{padding: 1 + 'em'}}><label>State : </label>{ address.state }</span> 
                                    : ''}
                                </div>
                                :
                                <div> 
                                    Not Available  
                                    <input type="button" value="ADD" onClick={() => this.setState({editMode : 'Y'})}/>
                                </div>
                            }
                        </div>
                    :
                        <div>
                            updating
                            <input type="button" value="UPDATE" onClick={() => this.setState({editMode : 'N'})}/>
                        </div>
                    }
                </div>
        )
    }
}

export default Address
