import React, { Component } from 'react';
import axios from 'axios';
// import { withRouter } from 'react-router-dom';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faAddressCard, faEdit, faPlus, faCopy } from '@fortawesome/free-solid-svg-icons'

class Admin extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            members : [],
            fromMember : {},
            fromMemberAddress : {},
            copyToMembers :[]
        }
    }

    fetchMembers = () => {
        axios.get(`http://localhost:8093/familytree/member`)
          .then(response => {
            console.log(response.data);
            this.setState({fromMemberAddress:response.data})
          }).catch(error =>{
              console.log(error);
          });
      }

    fetchAddress = (memberId) => {
        axios.get(`http://localhost:8093/familytree/member/{id}/address`)
          .then(response => {
            console.log(response.data);
            this.setState({members:response.data})
          }).catch(error =>{
              console.log(error);
          });
    }

    componentDidMount() {
        this.fetchMembers();
      };

    fromMemberChangeHandler = (event) => {
        console.log("changing the from member");
        if("NA" === event.target.value){
            this.statefromMember = null;
        }else{
            this.setState({fromMember : this.state.members[event.target.value]})
        }
    }
    
    render() {
        return (
            <div className="page-style">
                <h3>Welcome Ravi..</h3>
               
                <div>
                    <div><label style={{padding: 1 + 'em'}}>From Member</label>
                        <select style={{width: 28 + 'em'}} className="form-entity" name="fromMember" 
                                onChange={this.fromMemberChangeHandler}>
                            <option  value='NA'>Not Defined</option>
                            {this.state.members.map((member, index) => (
                                <option key={index} value={index}>{member.firstName} {member.lastName}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div>
                    <pre>{JSON.stringify(this.state.fromMember, null, 2) }</pre>
                    <pre>{JSON.stringify(this.state.fromMemberAddress, null, 2) }</pre>
                </div>
            </div>
        )
    }
}

export default Admin
