import React, { Component } from 'react'

class MemberForm extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            member : {}
        }
    }

    myChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({
            [name]: value,
            member : {[name]: value}
        });
        console.log(this.state)
    }
    
    render() {
        console.log(this.state.member)
        return (
            <div>
                <form>
                    <h1>Hello</h1>
                    <input type="text" name="firstName" onChange={this.myChangeHandler}  placeholder='First Name'/>
                    <input type="text" name="nickName" onChange={this.myChangeHandler}  placeholder='Nick Name'/>
                    <input type="text" name="lastName" onChange={this.myChangeHandler}  placeholder='Last Name'/>
                    <input type="text" name="about" onChange={this.myChangeHandler}  placeholder='about'/>
                    <br /><label>Married  : <input name="married" type="checkbox" onChange={this.handleInputChange} /></label>{this.state.married}
                </form>
            </div>
        )
    }
}

export default MemberForm;
