import React, { Component } from 'react'
import axios from 'axios';

class MemberForm extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            member : {"gender":"MALE"},
            fathers : [],
            mothers : []
        }
    }

    myChangeHandler = (event) => {
        console.log(event)
        let name = event.target.name;
        let value = event.target.type === 'checkbox' ? event.target.checked : event.target.value;
        this.setState({
            [name]: value,
        });
        this.state.member[name] = value;
    }

    parentChangeHandler = (event) => {
        console.log("inside parent change handler")
        let name = event.target.name;
        console.log(name);
        console.log(event.target.value);
        this.setState({});
        this.state.member[name] ={"id":event.target.value}
    }

    componentDidMount() {
        this.fetchFathers();
        this.fetchMothers();
        console.log(this.props.match)
        let id = this.props.match.params.id;
        if(id){
            console.log("this is update request");
            this.fetchMemberDetails();
        }
      };

      fetchMemberDetails () {
        axios.get(`http://localhost:8093/familytree/member/${this.props.match.params.id}`)
          .then(response => {
            console.log(response.data);
            this.setState({member:response.data})
          }).catch(error =>{
              console.log(error);
              //move to new
          });
      }

      fetchFathers () {
        axios.get(`http://localhost:8093/familytree/member/parents?married=true&gender=MALE`)
          .then(response => {
            console.log(response.data);
            this.setState({fathers:response.data})
          }).catch(error =>{
              console.log(error);
          });
      }

      fetchMothers () {
        axios.get(`http://localhost:8093/familytree/member/parents?married=true&gender=FEMALE`)
          .then(response => {
            console.log(response.data);
            this.setState({mothers:response.data})
          }).catch(error =>{
              console.log(error);
          });
      }

    createNew = () => {
        const { member } = this.state
        console.log(member)
        axios.post(`http://localhost:8093/familytree/member`,  member )
            .then(res => {
                console.log(res);
                console.log(res.data);
            }).catch(error =>{
                console.log(error);
            });
    }
    
    render() {
        console.log(this.state.member)
        return (
            <div className="page-style">
                <form>
                    <h2 style={{paddingLeft: 0.5 + 'em'}}>New Member</h2>
                    <input type="text" name="firstName" value={this.state.member.firstName} onChange={this.myChangeHandler}  placeholder='First Name'/>
                    <input type="text" name="nickName" value={this.state.member.nickName} onChange={this.myChangeHandler}  placeholder='Nick Name'/>
                    <input type="text" name="lastName" value={this.state.member.lastName} onChange={this.myChangeHandler}  placeholder='Last Name'/>
                    <input type="text" name="about" value={this.state.member.about} onChange={this.myChangeHandler}  placeholder='about'/>
                    <select className="form-entity" name="gender" value={this.state.member.gender} onChange={this.myChangeHandler}>
                        <option value="MALE">Male</option>
                        <option value="FEMALE">Female</option>
                    </select>
                    <div>
                        <label style={{padding: 1 + 'em'}}>Married  : <input name="married" type="checkbox" value={this.state.member.married} onChange={this.myChangeHandler} /></label>
                        {this.state.member.married ? <span><label>Anniversary Date</label><input style={{width: 17 + 'em'}} type="date" className="form-entity" name="weddingAnniversaryDate" onChange={this.myChangeHandler}></input></span> : ''}
                        {this.state.member.married ? 
                        <div>
                            <label style={{padding: 1 + 'em'}}>Life Partner :</label>
                            <select style={{width : 31 + 'rem'}} className="form-entity" name="mother" onChange={this.myChangeHandler}>
                                <option value="MALE">Male</option>
                                <option value="FEMALE">Female</option>
                            </select>
                        </div> : '' }
                    </div>
                    <div>
                        <label style={{padding: 1 + 'em'}}>Birth Date</label>
                        <input style={{width: 26 + 'em'}} type="date" className="form-entity" name="dateOfBirth" onChange={this.myChangeHandler}></input>
                    </div>
                    <div><label style={{padding: 1 + 'em'}}>Pappa</label>
                        <select style={{width: 28 + 'em'}} className="form-entity" name="father" 
                                value={this.state.member.father ? this.state.member.father.id : ''} 
                                onChange={this.parentChangeHandler}>
                            {this.state.fathers.map(father => (
                                <option key={father.id} value={father.id}>{father.firstName} {father.lastName}</option>
                            ))}
                        </select>
                    </div>
                    <div><label style={{padding: 1 + 'em'}}>Mamma</label>
                        <select style={{width: 28 + 'em'}} className="form-entity" name="mother" 
                        value={this.state.member.father ? this.state.member.father.id : ''} 
                        onChange={this.parentChangeHandler}>
                            {this.state.mothers.map(mother => (
                                <option key={mother.id} value={mother.id}>{mother.firstName} {mother.lastName}</option>
                            ))}
                        </select>
                    </div>
                    <div style={{padding: 1 + 'em'}}><input style={{width : 42 + 'rem'}} type="button" value="Create" onClick={this.createNew}></input></div>
                </form>
                <div><pre>{JSON.stringify(this.state.member.father, null, 2) }</pre></div>
                <div><pre>{JSON.stringify(this.state.member, null, 2) }</pre></div>
            </div>
        )
    }
}

export default MemberForm;
