import React, { Component } from 'react';
import axios from 'axios';
// import { withRouter } from 'react-router-dom';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faAddressCard, faEdit, faPlus, faCopy } from '@fortawesome/free-solid-svg-icons'

class Admin extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            members : []
        }
    }

    fetchMembers = () => {
        axios.get(`http://localhost:8093/familytree/member`)
          .then(response => {
            console.log(response.data);
            this.setState({members:response.data})
          }).catch(error =>{
              console.log(error);
          });
      }
    
    render() {
        return (
            <div className="page-style">
                <h3>Welcome Ravi..</h3>
                <div>
                    <div><label style={{padding: 1 + 'em'}}>From Member</label>
                        <select style={{width: 28 + 'em'}} className="form-entity" name="fromMember" 
                                value={this.state.member ? this.state.member.id : ''} 
                                onChange={this.parentChangeHandler}>
                            <option  value='NA'>Not Defined</option>
                            {this.state.members.map(member => (
                                <option key={member.id} value={member.id}>{member.firstName} {member.lastName}</option>
                            ))}
                        </select>
                    </div>

                </div>
            </div>
        )
    }
}

export default Admin
