import React, { Component } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

export class Birthdays extends Component {

    constructor(props) {
        super(props)
        this.state = {
            upcomingBirthdayMembers :[],
            recentBirthdayMembers :[],
            upcomingDays:30
        }
    };

    componentDidMount() {
        this.fetchBirthdayMembers(30, true);
        this.fetchBirthdayMembers(30, false);
      };

      fetchBirthdayMembers (days, upcoming) {
        axios.get(`http://localhost:8093/familytree/member/birthdays?days=${days}&upcoming=${upcoming}`)
          .then(response => {
            console.log(response.data);
            if(upcoming){
                this.setState({upcomingBirthdayMembers:response.data})
            }else{
                this.setState({recentBirthdayMembers:response.data})
            }
          }).catch(error =>{
              console.log(error);
          });
      }

      fetchUpcoming = () => {
        console.log("fetch upcoming");
        this.fetchBirthdayMembers(this.state.upcomingDays, true);
    }

    render() {
        return (
            <div>
                <div className="upcoming">
                    <h3 style={{display:'inline-block'}}>Upcoming Birthdays</h3>
                    <div style={{display:'inline-block'}}>
                        <input style={{maxWidth:2 + 'em'}} type='number' value={this.state.upcomingDays} onChange={(event) => { this.setState({upcomingDays: event.target.value}) }}/> 
                        <span style={{paddingLeft: 1 + 'em'}} onClick={this.fetchUpcoming}><FontAwesomeIcon icon={faSearch} /></span>
                    </div>
                    {this.state.upcomingBirthdayMembers.map(member => (
                        <div key={member.id}>{member.dateOfBirth} : {member.firstName}</div>
                    ))}
                </div>
                <div className="recent">
                    <h3>Recent Birthdays</h3>
                    {this.state.recentBirthdayMembers.map(member => (
                        <div key={member.id}>{member.dateOfBirth} : {member.firstName}</div>
                    ))}
                </div>
            </div>
        )
    }
}

export default Birthdays
