import React, { Component } from 'react';
import axios from 'axios';
// import { withRouter } from 'react-router-dom';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faAddressCard, faEdit, faPlus, faCopy } from '@fortawesome/free-solid-svg-icons'

class Admin extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            members : [],
            fromMember : {},
            copyToMembers :[]
        }
    }

    fetchMembers = () => {
        axios.get(`http://localhost:8093/familytree/member`)
          .then(response => {
            console.log(response.data);
            this.setState({members:response.data})
          }).catch(error =>{
              console.log(error);
          });
      }

      componentDidMount() {
        this.fetchMembers();
      };

      fromMemberChangeHandler = (event) => {
        if("NA" === event.target.value){
            this.statefromMember = null;
        }else{
            this.state.fromMember =event.target.value
        }
    }
    
    render() {
        return (
            <div className="page-style">
                <h3>Welcome Ravi..</h3>
                <div>
                    <div><label style={{padding: 1 + 'em'}}>From Member</label>
                        <select style={{width: 28 + 'em'}} className="form-entity" name="fromMember" 
                                value={this.state.fromMember ? this.state.fromMember : ''} 
                                onChange={this.fromMemberChangeHandler}>
                            <option  value='NA'>Not Defined</option>
                            {this.state.members.map(member => (
                                <option key={member.id} value={member}>{member.firstName} {member.lastName}</option>
                            ))}
                        </select>
                    </div>
                    
                </div>
            </div>
        )
    }
}

export default Admin
