import React, { Component } from 'react'
import axios from 'axios';

class MemberForm extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            member : {"gender":"MALE"}
        }
    }

    myChangeHandler = (event) => {
        console.log(event)
        let name = event.target.name;
        let value = event.target.type === 'checkbox' ? event.target.checked : event.target.value;
        this.setState({
            [name]: value,
        });
        this.state.member[name] = value;
    }

    createNew = () => {
        const { member } = this.state
        console.log(member)
        axios.post(`http://localhost:8093/familytree/member`,  member )
            .then(res => {
                console.log(res);
                console.log(res.data);
            }).catch(error =>{
                console.log(error);
            });
    }
    
    render() {
        console.log(this.state.member)
        return (
            <div className="page-style">
                <form>
                    <h2 style={{paddingLeft: 0.5 + 'em'}}>New Member</h2>
                    <input type="text" name="firstName" onChange={this.myChangeHandler}  placeholder='First Name'/>
                    <input type="text" name="nickName" onChange={this.myChangeHandler}  placeholder='Nick Name'/>
                    <input type="text" name="lastName" onChange={this.myChangeHandler}  placeholder='Last Name'/>
                    <input type="text" name="about" onChange={this.myChangeHandler}  placeholder='about'/>
                    <br /><select className="form-entity" name="gender" onChange={this.myChangeHandler}>
                        <option value="MALE">Male</option>
                        <option value="FEMALE">Female</option>
                    </select>
                    <div  style={{paddingLeft: 1 + 'em'}}>
                        <label>Married  : <input name="married" type="checkbox" onChange={this.myChangeHandler} /></label>
                        <input type="date" className="form-entity" name="weddingAnniversaryDate" onChange={this.myChangeHandler}></input>
                    </div>
                    <input type="date" className="form-entity" name="dateOfBirth" onChange={this.myChangeHandler}></input>
                    <input className="form-entity" type="button" value="Create" onClick={this.createNew}></input>
                </form>
                <div><pre>{JSON.stringify(this.state.member, null, 2) }</pre></div>
            </div>
        )
    }
}

export default MemberForm;
