import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAddressCard } from '@fortawesome/free-solid-svg-icons'

class Address extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             address : this.props.address,
             editMode : false
        }
    }
    

    render() {
        const { address } = this.state;
        const { editMode } = this.state;
        return (
                <div>
                    {address.id ? 
                        <div>
                            <i><FontAwesomeIcon icon={faAddressCard} /></i> 
                            {address.type ? 
                                <div><label>Type : </label>{ address.type }</div> 
                            : ''}
                            {address.line1 ? 
                                <div><label>Line 1 : </label>{ address.line1 }</div> 
                            : ''}
                            {address.line2 ? 
                                <div><label>Line 2 : </label>{ address.line2 }</div> 
                            : ''}
                            {address.city ? 
                                <div><label>City : </label>{ address.city }</div> 
                            : ''}
                            {address.taluka ? 
                                <div><label>Taluka : </label>{ address.taluka }</div> 
                            : ''}
                            {address.district ? 
                                <div><label>District : </label>{ address.district }</div> 
                            : ''}
                            {address.state ? 
                                <div><label>State : </label>{ address.state }</div> 
                            : ''}
                            <input type="button" value="EDIT" onClick="() => {this.state = {editMode : true}}"/>
                        </div>
                        :
                        <div> NA </div>
                    }
                </div>
        )
    }
}

export default Address
